import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, BrowserRouter } from 'react-router-dom';
import RegistroUsuario from './components/RegistroUsuario';
import Login from './components/Login';
import ListadoUsuarios from './components/ListadoUsuarios';
import RegistroMateria from './components/RegistroMateria';
import ListadoMaterias from './components/ListadoMaterias';
import Navbar from './components/Navbar';
import ListadoGrupos from './components/ListadoGrupos';
import RegistroGrupo from './components/RegistroGrupo';
import ListadoAsistencias from './components/ListadoAsistencias';
import RegistroAsistencia from './components/RegistroAsistencia';
import Acercade from './components/acercade';



function App() {
  const [auth, setAuth] = useState(null);

  useEffect(() => {
    const storedAuth = localStorage.getItem('auth');
    if (storedAuth) {
      setAuth(JSON.parse(storedAuth));
    }
  }, []);

  const handleLogout = () => {
    setAuth(null);
    localStorage.removeItem('auth');
  };

  const handleAuth = (authData) => {
    setAuth(authData);
    localStorage.setItem('auth', JSON.stringify(authData));
  };

  const members = [
    { name: 'Dharma Carolina Virgen Susunaga', photo: 'https://scontent-qro1-2.xx.fbcdn.net/v/t39.30808-6/441068701_2522944691221047_4301136998613048241_n.jpg?_nc_cat=101&ccb=1-7&_nc_sid=5f2048&_nc_ohc=aAPteSUZDW0Q7kNvgHuPOQ_&_nc_ht=scontent-qro1-2.xx&oh=00_AYC6sN7Os8S6mhSr5P14T1yRI1z56ujZs9W90aBQEInJKw&oe=665C7C3F' },
    { name: 'Kevin Eduardo Gómez Salas', photo: 'https://media-qro1-1.cdn.whatsapp.net/v/t61.24694-24/438639173_464867452575111_71758109706386029_n.jpg?ccb=11-4&oh=01_Q5AaIBfgzq84MSm1ja2MjQ1lxBmpssUjvxJQFjWhoTruqWO5&oe=6666653D&_nc_sid=e6ed6c&_nc_cat=103' },
    { name: 'Jared Aramis Lucho Saunier', photo: 'https://scontent-qro1-2.xx.fbcdn.net/v/t1.6435-9/95293248_1552967391547748_3016823266804236288_n.jpg?_nc_cat=111&ccb=1-7&_nc_sid=5f2048&_nc_ohc=k37nafc2Y5UQ7kNvgHfhtFg&_nc_ht=scontent-qro1-2.xx&oh=00_AYAFXuOOvqImhHSph6x4_75Y5Tdbki2v0C3_XexuEMWVXg&oe=667E15B0' },
    { name: 'Angelica Mendoza Vargas', photo: 'https://scontent-qro1-1.xx.fbcdn.net/v/t1.6435-9/131980866_3561808710540800_539615373066992762_n.jpg?_nc_cat=104&ccb=1-7&_nc_sid=5f2048&_nc_ohc=NH9xOGAyrWUQ7kNvgHk_Xh6&_nc_ht=scontent-qro1-1.xx&oh=00_AYCJ7AHO4hBY7zStsb5nxLPpUwRodcyWgLF-N9qZX3nC5g&oe=667E0466' },
    { name: 'Valeria Socken Portela', photo: 'https://media-qro1-1.cdn.whatsapp.net/v/t61.24694-24/427781986_1882773495497569_8599696548451168925_n.jpg?ccb=11-4&oh=01_Q5AaIMilfcITmuSDW3SvS_afIlNuenM4RTRvM0ZQv-6nGhWS&oe=6663CACB&_nc_sid=e6ed6c&_nc_cat=101' },
  ];

  return (
    <Router>
      <div className="App">
        {!auth ? (
          <Login setAuth={handleAuth} />
        ) : (
          <>
            <Navbar handleLogout={handleLogout} />
            <div className="container mt-4">
              <BrowserRouter basename="/w24011203/frontend">
              <Routes>
                <Route path="/usuarios" element={<ListadoUsuarios />} />
                <Route path="/materias" element={<ListadoMaterias />} />
                <Route path="/registro-usuario" element={<RegistroUsuario />} />
                <Route path="/registro-materia" element={<RegistroMateria />} />
                <Route path="/grupos" element={<ListadoGrupos />} />
                <Route path="/registro-grupo" element={<RegistroGrupo />} />
                <Route path="/asistencias" element={<ListadoAsistencias />} />
                <Route path="/registro-asistencia" element={<RegistroAsistencia />} />
                <Route path="/acercade" element={<Acercade members={members} />} />
                <Route path="/" element={<Navigate to="/usuarios" />} />
              </Routes>
              </BrowserRouter>
            </div>
          </>
        )}
      </div>
    </Router>
  );
}

export default App;
